import React, { useContext, useState, useRef } from 'react';
import gql from 'graphql-tag';
import { useQuery, useMutation } from '@apollo/react-hooks';
import moment from 'moment';
import {
  Button,
  Card,
  Form,
  Grid,
  Image,
  Icon,
  Label,
  Menu, 
  Segment
} from 'semantic-ui-react';

import { AuthContext } from '../context/auth';
import { useForm } from '../util/hooks';
import LikeButton from '../components/LikeButton';
import DeleteButton from '../components/DeleteButton';
import MyPopup from '../util/MyPopup';

function Profile(props) {

  const context = useContext(AuthContext);

  /*
  let postId = null
  console.log("=======1.0======");
  if (context.user===null)
  {
      console.log("No user");
      //postId = "5fe723cc2dd0c558e547ea89";
      //postId = "5fdf32d0d064dd1689ac3b8c";

      postId = "5fe40df82dd0c558e547ea88";      
      //post id is postid, but it is not the user id
      console.log(postId);
  }
  else
  {
    console.log("=======1.0.1.1=====");
    console.log(context.user.id);
    postId = context.user.id;
    username = context.user.username;
    console.log(username); 
  }
  console.log("=======1.0.1=====");

  //*/

  const username = context.user.username;
  console.log(username); 


  const { user } = useContext(AuthContext);
  const commentInputRef = useRef(null);

  const [comment, setComment] = useState('');

  const {
    loading,
    data: { getUserPosts: posts }
  } = useQuery(FETCH_USER_QUERY, {
    variables: {
      username
    }
  });

  function deletePostCallback() {
    props.history.push('/');
  }




  let postMarkup;
  if (!getPost)
  {
    postMarkup = <p>Loading post..</p>;
  }
  else 
  {
    const {
      id,
      body,
      createdAt,
      username,
      comments,
      likes,
      likeCount,
      commentCount
    } = getPost;

    postMarkup = (
      <div>
        <Grid centered columns={2}>
          <Grid.Column centered width={10}>
            <Card>
              <Image src='https://react.semantic-ui.com/images/avatar/large/daniel.jpg' wrapped ui={false} />
              <Card.Content>
                <Card.Header>Daniel</Card.Header>
                <Card.Meta>Joined in 2016</Card.Meta>
                <Card.Description>
                  Daniel is a comedian living in Nashville.
                </Card.Description>
              </Card.Content>
              <Card.Content extra>
                <a>
                  <Icon name='user' />
                  10 Friends
                </a>
              </Card.Content>
            </Card>
          </Grid.Column>
          <Grid.Column centered width={10}>
            <Grid.Row>
            {user && (
              <Grid.Column>
                  <PostForm />
                </Grid.Column>
              )}
              {loading ? (
                <h1>Loading posts..</h1>
              ) : (
                <Transition.Group>
                  {posts &&
                    posts.map((post) => (
                      <Grid.Column key={post.id} style={{ marginBottom: 20 }}>
                        <PostCard post={post} />
                      </Grid.Column>
                    ))}
                </Transition.Group>
              )}
            </Grid.Row>
          </Grid.Column>
        </Grid>
      </div>
    );
  }
  return postMarkup;

}

const LOGIN_USER = gql`
  mutation login($username: String!, $password: String!) {
    login(username: $username, password: $password) {
      id
      email
      username
      createdAt
      token
    }
  }
`;

const SUBMIT_COMMENT_MUTATION = gql`
  mutation($postId: String!, $body: String!) {
    createComment(postId: $postId, body: $body) {
      id
      comments {
        id
        body
        createdAt
        username
      }
      commentCount
    }
  }
`;

const FETCH_POST_QUERY = gql`
  query($postId: ID!) {
    getPost(postId: $postId) {
      id
      body
      createdAt
      username
      likeCount
      likes {
        username
      }
      commentCount
      comments {
        id
        username
        createdAt
        body
      }
    }
  }
`;

const FETCH_USER_QUERY = gql`
  query($username: String!) {
    getUserPosts(username: $username) {
      id
      body
      createdAt
      username
      likeCount
      likes {
        username
      }
      commentCount
      comments {
        id
        username
        createdAt
        body
      }
    }
  }
`;

export default Profile;
