import React, { useContext, useState, useEffect} from 'react';
import { useQuery } from '@apollo/react-hooks';
import { Grid, Transition,Dropdown, Divider, Header, Icon } from 'semantic-ui-react';

import { AuthContext } from '../context/auth';
import PostCard from '../components/PostCard';
import PostForm from '../components/PostForm';
import { FETCH_POSTS_QUERY, FETCH_AREA_QUERY } from '../util/graphql';


function Home() {

  const { user } = useContext(AuthContext);
  const [thoughtArea, setthoughtArea] = useState("default");
  /*
  const {
    loading,
    data: { getPosts: posts } 
  } = useQuery(FETCH_POSTS_QUERY);
  //*/

  console.log("====window.myVar======");
  console.log(window.myVar);
  useEffect(() => {
      console.log("====useEffect0.1======");
      window.myVar == null ? setthoughtArea("default") : setthoughtArea(window.myVar) ;
      console.log(thoughtArea);
  });


  const {
    loading,
    data: { getAreaPosts: posts } 
  } = useQuery(FETCH_AREA_QUERY,
    {
      variables: {
        thoughtArea
    },
  }
  );

  return (
    <Grid columns={1}>
      <Grid.Row>
        {user && (
          <Grid.Column>
            <PostForm />
          </Grid.Column>
        )}
        {loading ? (
          <h1>loading...</h1>
        ) : (
          <Transition.Group>
            <Grid.Column>
              <Divider horizontal><h3>最新</h3></Divider>
            </Grid.Column>
            {posts &&
              posts.map((post) => (
                <Grid.Column key={post.id} style={{ marginBottom: 20 }}>
                  <PostCard post={post} />
                </Grid.Column>
              ))}
          </Transition.Group>
        )}
      </Grid.Row>
    </Grid>
  );
}

export default Home;
