import React, { useContext,useState,useEffect} from 'react';
import { useQuery } from '@apollo/react-hooks';
import { Grid, Transition,Dropdown, Divider, Header, Icon } from 'semantic-ui-react';

import { AuthContext } from '../context/auth';
import PostCard from '../components/PostCard';
import PostForm from '../components/PostForm';
import { FETCH_POSTS_QUERY } from '../util/graphql';

const options = [
  { key: '1', text: '个人修养', value: 'self_improvement' },
  { key: '2', text: '人际关系', value: 'relationship' },
  { key: '3', text: '世界关系', value: 'wolrd' },
  { key: '4', text: '健康', value: 'Health' },
  { key: '5', text: '成功', value: 'Chinese' },
  { key: '6', text: '梦想', value: 'English' },
  { key: '7', text: '改变世界', value: 'French' },
  { key: '8', text: '勇敢去做', value: 'Spanish' },
  { key: '9', text: 'Innovative Ideas', value: 'German' },
  { key: '10', text: '工作', value: 'Chinese' },
  { key: '11', text: '开公司', value: 'English' },
  { key: '12', text: '干', value: 'French' },
  { key: '13', text: '时间 Time', value: 'Spanish' },
  { key: '14', text: '痛苦 Pain', value: 'German' },
  { key: '15', text: '财富 Wealth', value: 'Chinese' },
  { key: '16', text: '说话', value: 'English' },
  { key: '17', text: '家庭', value: 'French' },
  { key: '18', text: '年轻', value: 'Spanish' },
  { key: '19', text: '一念之差', value: 'German' },
  { key: '20', text: '借口', value: 'Chinese' },
  { key: '21', text: '善与恶', value: 'English' },
  { key: '22', text: '对付坏人', value: 'French' },
  { key: '23', text: '正义 Justice', value: 'Spanish' },
  { key: '24', text: '纪念 Memory', value: 'German' },
  { key: '25', text: '今天', value: 'Chinese' },
  { key: '26', text: '计划 Plan', value: 'English' },
  { key: '27', text: '革命', value: 'French' },
]

function Home() {
  const { user } = useContext(AuthContext);
  const {
    loading,
    data: { getPosts: posts } 
  } = useQuery(FETCH_POSTS_QUERY);

  const [state,setState] = useState({options});
  const [currentValues,setCurrentValues] = useState([]);

  const handleAddition = (e, { value }) => {
  };



  const handleChange = (e, { value }) => {console.log(currentValues);setCurrentValues(value)};


  return (
    <Grid columns={1}>
      <Grid.Row>
        <Grid.Column>
          <Divider horizontal>
            <Header as='h3'>
              <Icon name='search' color="blue"/>
                搜索 思想 领域
            </Header>
          </Divider>
        </Grid.Column>
      <Grid.Column>
        <Dropdown
          options={state.options}
          placeholder='搜索 思想 领域'
          search
          fluid
          selection
          multiple
          allowAdditions
          value={currentValues}
          onAddItem={null}
          onChange={handleChange}
        />
        </Grid.Column>
      </Grid.Row>
      <Grid.Row>
        {user && (
          <Grid.Column>
            <PostForm />
          </Grid.Column>
        )}
        {loading ? (
          <h1>loading...</h1>
        ) : (
          <Transition.Group>
            <Grid.Column>
              <Divider horizontal><h3>最新</h3></Divider>
            </Grid.Column>
            {posts &&
              posts.map((post) => (
                <Grid.Column key={post.id} style={{ marginBottom: 20 }}>
                  <PostCard post={post} />
                </Grid.Column>
              ))}
          </Transition.Group>
        )}
      </Grid.Row>
    </Grid>
  );
}

export default Home;
