import React,{useState,useEffect} from 'react';
import { Button, Form, Divider, Header, Icon, Grid, Dropdown } from 'semantic-ui-react';
import gql from 'graphql-tag';
import { useMutation } from '@apollo/react-hooks';

import { useForm } from '../util/hooks';
import { FETCH_POSTS_QUERY } from '../util/graphql';

const options = [
  { key: '1', text: '个人修养', value: 'Self_improvement' },
  { key: '2', text: '人际关系', value: 'Relationship' },
  { key: '3', text: '世界关系', value: 'Wolrd' },
  { key: '4', text: '健康', value: 'Health' },
  { key: '5', text: '成功', value: 'Success' },
  { key: '6', text: '梦想', value: 'Dream' },
  { key: '7', text: '改变世界', value: 'Change_the_world' },
  { key: '8', text: '勇敢去做', value: 'Brave_to_do' },
  { key: '9', text: 'Innovative Ideas', value: 'Innovative Ideas' },
  { key: '10', text: '工作', value: 'Job' },
  { key: '11', text: '开公司', value: 'Company' },
  { key: '12', text: '干', value: 'do' },
  { key: '13', text: '时间 Time', value: 'Time' },
  { key: '14', text: '痛苦 Pain', value: 'Pain' },
  { key: '15', text: '财富 Wealth', value: 'Wealth' },
  { key: '16', text: '说话', value: 'Speak' },
  { key: '17', text: '家庭', value: 'Family' },
  { key: '18', text: '年轻', value: 'Young' },
  { key: '19', text: '一念之差', value: 'Danger_thought' },
  { key: '20', text: '借口', value: 'Excuse' },
  { key: '21', text: '善与恶', value: 'good_or_evil' },
  { key: '22', text: '对付坏人', value: 'Enemy' },
  { key: '23', text: '正义 Justice', value: 'Justice' },
  { key: '24', text: '纪念 Memory', value: 'Memory' },
  { key: '25', text: '今天', value: 'Today' },
  { key: '26', text: '计划 Plan', value: 'Plan' },
  { key: '27', text: '革命', value: 'Revolution' },
]



function PostForm() {
  //const { values, onChange, onSubmit } = useForm(createPostCallback, {
  //  body: ''
  //});

  const [values, setValues] = useState('');
  const [texts, setTexts] = useState('');
  const [thoughtArea, setThoughtArea] = useState('');

  const onChange = (event) => {
    //setValues({ ...values, [event.target.name]: event.target.value });
    setTexts({ ...texts, [event.target.name]: event.target.value });
    //console.log("=========1.0.0.0.1========");
    //console.log(event.target.name);
    //console.log(event.target.value);
    //console.log({ ...texts, [event.target.name]: event.target.value });
    //console.log(texts);

    setThoughtArea({ ...thoughtArea, ["thoughtArea"]: "thoughtArea"});
    //console.log({ ...thoughtArea, ["thoughtArea"]: "thoughtArea"});
  };

  const onSubmit = (event) => {
    event.preventDefault();
    //console.log("=========1.0========");
    createPost();
    //createPost3({
    //  variables: {thoughtArea3},
    //});
  };

  /*
  const [createPost, { error }] = useMutation(CREATE_POST_MUTATION, {
    variables: values,
    update(proxy, result) {
      const data = proxy.readQuery({
        query: FETCH_POSTS_QUERY
      });
      data.getPosts = [result.data.createPost, ...data.getPosts];
      proxy.writeQuery({ query: FETCH_POSTS_QUERY, data });
      values.body = '';
    }
  });
//*/

  const [createPost, { error }] = useMutation(CREATE_POST_MUTATION);


  const [createAreaPost] = useMutation(CREATE_AREA_POST_MUTATION,{
    variables: { body: texts.body, thoughtArea: thoughtArea.thoughtArea },
    update(proxy, result) {
      const data = proxy.readQuery({
        query: FETCH_POSTS_QUERY
      });
      data.getPosts = [result.data.createPost, ...data.getPosts];
      proxy.writeQuery({ query: FETCH_POSTS_QUERY, data });
      values.body = '';
    }
  });

  const submit = (event) => {

    event.preventDefault();
    //console.log("=========1.0.0========");
    //console.log(texts,thoughtArea);
    //createPost3({
    //  variables: {thoughtArea3},
    //});
    //addTodo({ variables: { type: input.value } });
    //updateTodo({ variables: { id, type: input.value } });
    //createAreaPost({ variables: { body: texts.body, thoughtArea: thoughtArea.thoughtArea } });
    createAreaPost();

  };

  function createPostCallback() {
    //console.log("=========1.0.1.1========");
    createPost();
    //createPost3({
    //  variables: {thoughtArea3},
    //});
  }

  const [state,setState] = useState({options});
  const [currentValues,setCurrentValues] = useState([]);

    // Similar to componentDidMount and componentDidUpdate:
  //useEffect(() => {
  //   console.log("====useEffect1.0======");
  //    console.log(currentValues);
  //});


  //const handleAddition = (e, { value }) => {

  //  console.log("=========1.0==========");   
  //  console.log(value);
    //setState((prevState) => ({
    //  options: [{ text: value, value }, ...prevState.options],
    //}))
  //}



  const handleChange = (e, { value }) => 
  {
    //console.log(value);
    setCurrentValues(value)
  };

  return (
    <>
    <Grid columns={1}>
      <Grid.Row>
        <Grid.Column>
          <Divider horizontal>
            <Header as='h3'>
              <Icon name='search' color="blue"/>
                搜索 思想 领域
            </Header>
          </Divider>
        </Grid.Column>
      <Grid.Column>
        <Dropdown
          options={state.options}
          placeholder='搜索 思想 领域'
          search
          fluid
          selection
          allowAdditions
          scrolling
          value={currentValues}
          onAddItem={null}
          onChange={handleChange}
        />
        </Grid.Column>
      </Grid.Row>
    </Grid>
      <Form onSubmit={submit}>
          <Divider horizontal>
            <Header as='h3'>
              <Icon name='comments' color="blue"/>
                发表 你的 思想
            </Header>
          </Divider>
        <Form.Field>
          <Form.Input
            placeholder="世界 你好!"
            name="body"
            onChange={onChange}
            value={values.body}
            error={error ? true : false}
          />
          <Button type="submit" color="blue">
            发布
          </Button>
        </Form.Field>
      </Form>
      {error && (
        <div className="ui error message" style={{ marginBottom: 20 }}>
          <ul className="list">
            <li>{error.graphQLErrors[0].message}</li>
          </ul>
        </div>
      )}
    </>
  );
}

const CREATE_POST_MUTATION = gql`
  mutation createPost($body: String!) {
    createPost(body: $body) {
      id
      body
      createdAt
      username
      likes {
        id
        username
        createdAt
      }
      likeCount
      comments {
        id
        body
        username
        createdAt
      }
      commentCount
    }
  }
`;

const CREATE_AREA_POST_MUTATION = gql`
  mutation createAreaPost($body: String!,$thoughtArea: String!) {
    createAreaPost(body: $body, thoughtArea: $thoughtArea) {
      id
      body
      createdAt
      username
      likes {
        id
        username
        createdAt
      }
      likeCount
      comments {
        id
        body
        username
        createdAt
      }
      commentCount
      thoughtArea
    }
  }
`;

export default PostForm;
